import AbstractOperationHandler from "../chain/abstract_operation_handler";
import AddOperationHandler from "../chain/add_operation_handler";
import DivideOperationHandler from "../chain/divide_operation_handler";
import MultiplyOperationHandler from "../chain/multiply_operation_handler";
import OperandHandler from "../chain/operand_handler";
import SubstractOperationHandler from "../chain/substract_operation_handler";
import NumberOperand from "../operand/number_operand";

export default class CalcCommandReceiver {
  constructor() {
    this.displayView = null;
    this.equationView = null;

    this.model = null;
  }

  updateDisplayView() {
    this.displayView.redraw();
  }

  updateEquationView() {
    this.equationView.redraw();
  }

  updateView() {
    this.updateDisplayView();
    this.updateEquationView();
  }

  setEquationView(equationView) {
    this.equationView = equationView;
  }

  setDisplayView(displayView) {
    this.displayView = displayView;
  }

  actionOperator(actionCommand) {
    if (actionCommand === "+") {
      this.addHandler(new AddOperationHandler(null));
    } else if (actionCommand === "-") {
      this.addHandler(new SubstractOperationHandler(null));
    } else if (actionCommand === "*") {
      this.addHandler(new MultiplyOperationHandler(null));
    } else if (actionCommand === "/") {
      this.addHandler(new DivideOperationHandler(null));
    }
  }

  actionEqual() {
    let resultText = this.getResultText();

    let numberOperand = new NumberOperand(null);
    numberOperand.append(resultText);

    let newOperandHandler = new OperandHandler(numberOperand);

    this.initHandler();
    this.addHandler(newOperandHandler);
  }

  actionNumber(actionCommand) {
    let lastHandler = this.getLastHandler();
    if (lastHandler instanceof AbstractOperationHandler) {
      let input = actionCommand;

      let numberOperand = new NumberOperand(input);
      this.setLastOperand(numberOperand);
    } else {
      this.appendInputToDisplay(actionCommand);
    }
  }

  setModel(model) {
    this.model = model;
  }

  addHandler(handler) {
    this.model.addHandler(handler);
  }

  appendInputToDisplay(input) {
    let lastOperand = this.getLastOperand();

    if (lastOperand !== null) {
      if (lastOperand.isNumber()) {
        lastOperand.append(input);
      }
    }
  }

  getLastHandler() {
    return this.model.getLastHandler();
  }

  getLastOperand() {
    const lastHandler = this.getLastHandler();

    if (lastHandler !== null) {
      return lastHandler.getOperand();
    }

    return null;
  }

  getResultExceptLast() {
    return this.model.getResultExceptLast();
  }

  getResultText() {
    return this.model.getResultText();
  }

  initHandler() {
    this.model.initHandler();
  }

  replaceHandler(handler) {
    this.model.replaceHandler(handler);
  }

  setLastOperand(operand) {
    let lastHandler = this.getLastHandler();

    if (lastHandler !== null) {
      lastHandler.setOperand(operand);
    }
  }
}
