import CalcUtil from "../app/calc_util";
import AbstractOperationHandler from "../chain/abstract_operation_handler";
import OperandHandler from "../chain/operand_handler";
import Request from "../chain/request";
import NumberOperand from "../operand/number_operand";

export default class CalcModel {
  constructor() {
    this.handlerList = [];

    let operandHandler = new OperandHandler(new NumberOperand(null));
    this.handlerList.push(operandHandler);
  }

  getDisplayEquationText() {
    let lastHandler = this.getLastHandler();

    if (lastHandler instanceof AbstractOperationHandler) {
      return this.getEquationText();
    } else {
      return this.getEquationTextExceptInput();
    }
  }

  getDisplayText() {
    let lastHandler = this.getLastHandler();

    if (lastHandler instanceof AbstractOperationHandler) {
      return this.getResultText();
    } else {
      return this.getInputText();
    }
  }

  invalidateChain() {
    for (const handler of this.handlerList) {
      handler.setNext(null);
    }
  }

  makeChain() {
    this.invalidateChain();

    let handler = null;
    let nextHandler = null;

    for (let index = 0; index < this.handlerList.length - 1; index++) {
      handler = this.handlerList[index];
      nextHandler = this.handlerList[index + 1];

      handler.setNext(nextHandler);
    }

    return this.handlerList[0];
  }

  getLastNumberOperand() {
    let lastOperand = this.getLastOperand();

    if (lastOperand !== null) {
      if (lastOperand.isNumber()) {
        return lastOperand;
      }
    }
    return null;
  }

  getLastOperand() {
    let lastHandler = this.getLastHandler();

    if (lastHandler !== null) {
      return lastHandler.getOperand();
    }
  }

  getLastHandler() {
    let size = this.handlerList.length;

    if (size > 0) {
      return this.handlerList[size - 1];
    }

    return null;
  }

  getResult() {
    let operandHandler = this.makeChain();

    let request = new Request();

    operandHandler.handleRequest(request);

    let result = request.getResult();

    return result;
  }

  getResultExceptLast() {
    let operandHandler = this.makeChainExceptLast();

    let request = new Request();

    operandHandler.handleRequest(request);
    let result = request.getResult();

    return result;
  }

  getResultText() {
    let result = this.getResult();

    let resultText = CalcUtil.getDisplayNumber(result);
    return resultText;
  }

  getInputText() {
    let lastNumberOperand = this.getLastNumberOperand();

    if (lastNumberOperand !== null) {
      return lastNumberOperand.getInputText();
    }

    return null;
  }

  getEquationText() {
    let operandHandler = this.makeChain();

    let request = new Request();

    operandHandler.makeEquation(request);

    return request.getEquation();
  }

  getEquationTextExceptInput() {
    let lastHandler = this.getLastHandler();
    if (lastHandler.isOperation()) {
      let lastOperator = lastHandler.getOperator();

      let operandHandler = this.makeChainExceptLast();

      let req = new Request();

      operandHandler.makeEquation(req);

      return req.getEquation() + "" + lastOperator + "";
    }

    return "";
  }

  addHandler(handler) {
    this.handlerList.push(handler);
  }

  initHandler() {
    this.handlerList = [];
    let operandHandler = new OperandHandler(new NumberOperand(null));

    this.handlerList.push(operandHandler);
  }

  replaceHandler(handler) {
    let size = this.handlerList.length;

    if (size > 0) {
      this.handlerList.splice(size - 1, 1);
      this.handlerList.push(handler);
    }
  }
}
